{"nbformat":4,"nbformat_minor":0,"metadata":{"colab":{"provenance":[],"authorship_tag":"ABX9TyP1g230iNlvLmZ1JSlfrVEi"},"kernelspec":{"name":"python3","display_name":"Python 3"},"language_info":{"name":"python"}},"cells":[{"cell_type":"code","execution_count":null,"metadata":{"id":"k-4gZJhb0S6h"},"outputs":[],"source":["import streamlit as st\n","import pandas as pd\n","import joblib\n","\n","\n","@st.cache_resource\n","def load_model_and_encoder():\n","    model_path = \"salary_prediction_model (2).pkl\"\n","    encoder_path = \"label_encoder (1).pkl\"\n","    try:\n","        model = joblib.load(model_path)\n","        encoder = joblib.load(encoder_path)\n","        return model, encoder\n","    except FileNotFoundError:\n","        st.error(\n","            f\"Error: Model or encoder file not found. \"\n","            f\"Make sure '{model_path}' and '{encoder_path}' are in the correct directory.\"\n","        )\n","        st.stop()\n","    except Exception as e:\n","        st.error(f\"Error loading model or encoder: {e}\")\n","        st.stop()\n","\n","\n","model, encoder = load_model_and_encoder()\n","\n","st.title(\"Salary Prediction App\")\n","st.write(\"Enter the details below to predict the salary:\")\n","\n","age = st.number_input(\"Age\", min_value=18, max_value=100, value=30)\n","gender = st.selectbox(\"Gender\", encoder[\"Gender\"].classes_)\n","education = st.selectbox(\"Education Level\", encoder[\"Education Level\"].classes_)\n","job_title = st.selectbox(\"Job Title\", encoder[\"Job Title\"].classes_)\n","years_of_exp = st.number_input(\"Years of Experience\", min_value=0, max_value=50, value=5)\n","\n","input_data = pd.DataFrame({\n","    \"Age\": [age],\n","    \"Gender\": [gender],\n","    \"Education Level\": [education],\n","    \"Job Title\": [job_title],\n","    \"Years of Experience\": [years_of_exp]\n","})\n","\n","if st.button(\"Predict Salary\"):\n","\n","    df_encoded = input_data.copy()\n","    for col_name, label_encoder in encoder.items():\n","        if col_name in df_encoded.columns:\n","            try:\n","                df_encoded[col_name] = label_encoder.transform(\n","                    df_encoded[col_name]\n","                )\n","            except ValueError as e:\n","                st.error(f\"Error encoding '{col_name}': {e}\")\n","                st.stop()\n","\n","    try:\n","        prediction = model.predict(df_encoded)\n","        st.success(f\"Predicted Salary: ${prediction[0]:,.2f}\")\n","    except Exception as e:\n","        st.error(f\"Error during prediction: {e}\")\n"]},{"cell_type":"code","source":[],"metadata":{"id":"QXFGqyKYNFc_"},"execution_count":null,"outputs":[]},{"cell_type":"code","source":[],"metadata":{"id":"YoUE8zSBNFZn"},"execution_count":null,"outputs":[]},{"cell_type":"code","source":[],"metadata":{"id":"l4BwKZgoNFUg"},"execution_count":null,"outputs":[]}]}
